#include "stdlib/blas/base/csrot.h"
#include "stdlib/blas/base/shared.h"

/**
* Applies a plane rotation where the cos and sin (c and s) are real and the vectors are complex single-precision floating-point vectors.
*
* @param N        number of indexed elements
* @param CX        first input array
* @param strideX  CX stride length
* @param CY        second input array
* @param strideY  CY stride length
* @param c        cosine of the angle of rotation
* @param s        sine of the angle of rotation
*/
void API_SUFFIX(c_csrot)( const CBLAS_INT N, void *CX, const CBLAS_INT strideX, void *CY, const CBLAS_INT strideY, const float c, const float s ) {
	float *x = (float *)CX;
	float *y = (float *)CY;
	float tmp1;
	float tmp2;
	CBLAS_INT ix;
	CBLAS_INT iy;
	CBLAS_INT sx;
	CBLAS_INT sy;
	CBLAS_INT i;
	CBLAS_INT j;

	if ( N <= 0 ) {
		return;
	}
	if ( strideX == 1 && strideY == 1 ) {
		for ( i = 0; i < N*2; i += 2 ) {
			tmp1 = c*x[ i ] + s*y[ i ];
			y[ i ] = c*y[ i ] - s*x[ i ];
			x[ i ] = tmp1;

			j = i + 1;
			tmp2 = c*x[ j ] + s*y[ j ];
			y[ j ] = c*y[ j ] - s*x[ j ];
			x[ j ] = tmp2;
		}
		return;
	}
	if ( strideX < 0 ) {
		ix = 2 * (1-N) * strideX;
	} else {
		ix = 0;
	}
	if ( strideY < 0 ) {
		iy = 2 * (1-N) * strideY;
	} else {
		iy = 0;
	}
	sx = strideX * 2;
	sy = strideY * 2;
	for ( i = 0; i < N; i++ ) {
		tmp1 = c*x[ ix ] + s*y[ iy ];
		y[ iy ] = c*y[ iy ] - s*x[ ix ];
		x[ ix ] = tmp1;

		tmp2 = c*x[ ix+1 ] + s*y[ iy+1 ];
		y[ iy+1 ] = c*y[ iy+1 ] - s*x[ ix+1 ];
		x[ ix+1 ] = tmp2;

		ix += sx;
		iy += sy;
	}
	return;
}
