import dtrmv = require( './index' );


// TESTS //

// The function returns a Float64Array...
{
	const x = new Float64Array( 10 );
	const A = new Float64Array( 20 );

	dtrmv( 'row-major', 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectType Float64Array
}

// The compiler throws an error if the function is provided a first argument which is not a string...
{
	const x = new Float64Array( 10 );
	const A = new Float64Array( 20 );

	dtrmv( 10, 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectError
	dtrmv( true, 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectError
	dtrmv( false, 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectError
	dtrmv( null, 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectError
	dtrmv( undefined, 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectError
	dtrmv( [], 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectError
	dtrmv( {}, 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectError
	dtrmv( ( x: number ): number => x, 'upper', 'none', 'unit', 10, A, 10, x, 1 ); // $ExpectError
}

