// The compiler throws an error if the function is provided an unsupported number of arguments...
{
	const x = new Float64Array( 10 );
	const A = new Float64Array( 20 );

	dtrmv(); // $ExpectError
	dtrmv( 'row-major' ); // $ExpectError
	dtrmv( 'row-major', 'upper' ); // $ExpectError
	dtrmv( 'row-major', 'upper', 'none' ); // $ExpectError
	dtrmv( 'row-major', 'upper', 'none', 'unit' ); // $ExpectError
	dtrmv( 'row-major', 'upper', 'none', 'unit', 10 ); // $ExpectError
	dtrmv( 'row-major', 'upper', 'none', 'unit', 10, A ); // $ExpectError
	dtrmv( 'row-major', 'upper', 'none', 'unit', 10, A, 10 ); // $ExpectError
	dtrmv( 'row-major', 'upper', 'none', 'unit', 10, A, 10, x ); // $ExpectError
	dtrmv( 'row-major', 'upper', 'none', 'unit', 10, A, 10, x, 1, 1 ); // $ExpectError
}

// Attached to main export is an `ndarray` method which returns a Float64Array...
{
	const x = new Float64Array( 10 );
	const A = new Float64Array( 20 );

	dtrmv.ndarray( 'row-major', 'upper', 'none', 'unit', 10, A, 10, x, 1, 0 ); // $ExpectType Float64Array
}

