'use strict';

// MODULES //

var absf = require( '@stdlib/math/base/special/absf' );
var imagf = require( '@stdlib/complex/imagf' );
var realf = require( '@stdlib/complex/realf' );
var sqrtf = require( '@stdlib/math/base/special/sqrtf' );


// MAIN //

/**
* Finds the euclidean norm of a single-precision floating-point vector.
*
* @param {PositiveInteger} N - number of indexed elements
* @param {Complex64Array} cx - input array
* @param {NonNegativeInteger} strideX - `cx` stride length
* @returns {number} index value
*
* @example
* var Complex64Array = require( '@stdlib/array/complex64' );
*
* var cx = new Complex64Array( [ 0.3, 0.1, 0.5, 0.0, 0.0, 0.5, 0.0, 0.2, 2.0, 3.0 ] );
*
* var norm = scnrm2( 4, cx, 1 );
* // returns ~0.8
*/
function scnrm2( N, cx, strideX ) {
	var scale;
	var norm;
	var temp;
	var ssq;
	var ix;

	if ( N < 1 || strideX < 0 ) {
		return 0.0;
	}
	scale = 0.0;
	ssq = 1.0;
	for ( ix = 0; ix <= ( N - 1 ) * strideX; ix += strideX ) {
		if ( realf( cx.get( ix ) ) !== 0.0 ) {
			temp = absf( realf( cx.get( ix ) ) );
			if ( scale < temp ) {
				ssq = 1.0 + ( ssq * ( ( scale / temp ) * ( scale / temp ) ) );
				scale = temp;
			} else {
				ssq += ( ( temp / scale ) * ( temp / scale ) );
			}
		}
		if ( imagf( cx.get( ix ) ) !== 0.0 ) {
			temp = absf( imagf( cx.get( ix ) ) );
			if ( scale < temp ) {
				ssq = 1.0 + ( ssq * ( ( scale / temp ) * ( scale / temp ) ) );
				scale = temp;
			} else {
				ssq += ( ( temp / scale ) * ( temp / scale ) );
			}
		}
		norm = scale * sqrtf( ssq );
	}
	return norm;
}


// EXPORTS //

module.exports = scnrm2;
