#include "stdlib/blas/base/scnrm2.h"
#include "stdlib/blas/base/shared.h"
#include "stdlib/math/base/special/absf.h"
#include "stdlib/math/base/special/sqrtf.h"
#include <stdbool.h>

// Largest positive single-precision floating-point number
static const float maxN = 3.4028234663852886e38f;

// Blue's scaling constants...
static const float tsml = 1.08420217E-19f;

static const float tbig = 4.50359963E+15f;

static const float ssml = 3.77789319E+22f;

static const float sbig = 1.32348898E-23f;

/**
* Computes the L2-norm of a complex single-precision floating-point vector.
*
* @param N        number of indexed elements
* @param CX        input array
* @param strideX  CX stride length
*/
float API_SUFFIX(c_scnrm2)( const CBLAS_INT N, const void *CX, const CBLAS_INT strideX ) {
	float *x = (float *)CX;
	CBLAS_INT ix;
	CBLAS_INT sx;
	bool notbig;
	CBLAS_INT i;
	float sumsq;
	float abig;
	float amed;
	float asml;
	float norm;
	float ymax;
	float ymin;
	float scl;
	float ax;

	if ( N <= 0 ) {
		return 0.0f;
	}
	sumsq = 0.0f;
	notbig = true;
	abig = 0.0f;
	amed = 0.0f;
	asml = 0.0f;
	if ( strideX < 0 ) {
		ix = ( 1 - N ) * strideX * 2;
	}
	else {
		ix = 0;
	}
	sx = strideX * 2;
	for ( i = 0; i < N; i++ ) {
		ax = stdlib_base_absf( x[ ix ] );
		if ( ax > tbig ) {
			abig += ( ax * sbig ) * ( ax * sbig );
			notbig = false;
		} else if ( ax < tsml ) {
			if ( notbig ) {
				asml += ( ax * ssml ) * ( ax * ssml );
			}
		} else {
			amed += ( ax * ax );
		}
		ax = stdlib_base_absf( x[ ix + 1 ] );
		if ( ax > tbig ) {
			abig += ( ax * sbig ) * ( ax * sbig );
			notbig = false;
		} else if ( ax < tsml ) {
			if ( notbig ) {
				asml += ( ax * ssml ) * ( ax * ssml );
			}
		} else {
			amed += ( ax * ax );
		}
		ix += sx;
	}
	if ( abig > 0.0f ) {
		if ( amed > 0.0f || ( amed > maxN ) || ( amed != amed ) ) {
			abig += ( amed * sbig ) * sbig;
		}
		scl = ( 1.0f / sbig );
		sumsq = abig;
	} else if ( asml > 0.0f ) {
		if ( amed > 0.0f || amed > maxN || ( amed != amed ) ) {
			amed = stdlib_base_sqrtf( amed );
			asml = stdlib_base_sqrtf( asml ) / ssml;
			if ( asml > amed ) {
				ymin = amed;
				ymax = asml;
			} else {
				ymin = asml;
				ymax = amed;
			}
			scl = 1.0f;
			sumsq = ( ymax * ymax ) * ( 1.0 + ( ymin / ymax ) * ( ymin / ymax ) ); // eslint-disable-line max-len
		} else {
			scl = ( 1.0f / ssml );
			sumsq = asml;
		}
	} else {
		scl = 1.0f;
		sumsq = amed;
	}
	norm = stdlib_base_sqrtf( sumsq ) * scl;
	return norm;
}
