var abs = require( '@stdlib/math/base/special/abs' );
var sgemv = require( './../lib/ndarray.js' );


// FIXTURES //

var stridesxoyo = require( './fixtures/julia/strides_xoyo.json' );
var stridesxoyt = require( './fixtures/julia/strides_xoyt.json' );
