'use strict';

// MODULES //

var tape = require( 'tape' );
var Float32Array = require( '@stdlib/array/float32' );
var EPS = require( '@stdlib/constants/float32/eps' );
var abs = require( '@stdlib/math/base/special/abs' );
var sspmv = require( './../lib/sspmv.js' );


// FIXTURES //

var stridexoyt = require( './fixtures/julia/strides_xoyt.json' );
var stridexpyp = require( './fixtures/julia/strides_xpyp.json' );
var stridexnyp = require( './fixtures/julia/strides_xnyp.json' );
var stridexpyn = require( './fixtures/julia/strides_xpyn.json' );
var stridexnyn = require( './fixtures/julia/strides_xnyn.json' );


// FUNCTIONS //

/**
* Tests for element-wise approximate equality.
*
* @private
* @param {Object} t - test object
* @param {Collection} actual - actual values
* @param {Collection} expected - expected values
* @param {number} rtol - relative tolerance
*/
function isApprox( t, actual, expected, rtol ) {
	var delta;
	var tol;
	var i;

	t.strictEqual( actual.length, expected.length, 'returns expected value' );
	for ( i = 0; i < expected.length; i++ ) {
		if ( actual[ i ] === expected[ i ] ) {
			t.strictEqual( actual[ i ], expected[ i ], 'returns expected value' );
		} else {
			delta = abs( actual[ i ] - expected[ i ] );
			tol = rtol * EPS * abs( expected[ i ] );
			t.ok( delta <= tol, 'within tolerance. actual: '+actual[ i ]+'. expected: '+expected[ i ]+'. delta: '+delta+'. tol: '+tol+'.' );
		}
	}
}


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof sspmv, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function has an arity of 10', function test( t ) {
	t.strictEqual( sspmv.length, 10, 'returns expected value' );
	t.end();
});

tape( 'the function performs one of the matrix-vector operations matrix-vector operations `y = alpha*A*x + beta*y` where alpha and beta are scalars, x and y are n element vectors and A is an n by n symmetric matrix, supplied in packed form (sx=1, sy=1)', function test( t ) {
	var expected;
	var strideX;
	var strideY;
	var alpha;
	var order;
	var beta;
	var uplo;
	var out;
	var ap;
	var n;
	var x;
	var y;

	order = stridexpyp.order;
	uplo = stridexpyp.uplo;

	n = stridexpyp.N;

	strideX = stridexpyp.strideX;
	strideY = stridexpyp.strideY;

	alpha = stridexpyp.alpha;
	beta = stridexpyp.beta;

	ap = new Float32Array( stridexpyp.AP );
	x = new Float32Array( stridexpyp.x );
	y = new Float32Array( stridexpyp.y );

	expected = new Float32Array( stridexpyp.y_out );

	out = sspmv( order, uplo, n, alpha, ap, x, strideX, beta, y, strideY );
	isApprox( t, y, expected, 2.0 );
	t.strictEqual( out, y, 'returns expected value' );
	t.end();
});

