// TypeScript Version: 4.1

/**
* Interface describing `ssymv`.
*/
interface Routine {
	/**
	* Performs one of the matrix-vector operations `y = alpha*A*x + beta*y` where alpha and beta are scalars, x and y are n element vectors and A is an n by n symmetric matrix.
	*
	* @param order - storage layout
	* @param uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is being supplied
	* @param N - number of columns in the matrix `A`
	* @param alpha - scalar constant
	* @param A - matrix of coefficients
	* @param LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
	* @param x - an `M` element vector
	* @param strideX - `x` stride length
	* @param beta - scalar constant
	* @param y - an `N` element vector
	* @param strideY - `y` stride length
	* @returns output array `y`
	*
	* @example
	* var Float32Array = require( '@stdlib/array/float32' );
	*
	* var uplo = 'L';
	* var order = 'row-major';
	*
	* var N = 3;
	* var lda = 3;
	*
	* var alpha = 1.0;
	* var beta = 0.0;
	*
	* var A = new Float32Array( [ 1.0, 0.0, 0.0, 2.0, 4.0, 0.0, 3.0, 5.0, 6.0 ] );
	* var x = new Float32Array( [ 1.0, 1.0, 1.0 ] );
	* var y = new Float32Array( [ 0.0, 0.0, 0.0 ] );
	*
	* ssymv( order, uplo, N, alpha, A, lda, x, 1, beta, y, 1 );
	* // y => <Float32Array>[ 6.0, 11.0, 14.0 ]
	*/
	( order: string, uplo: string, N: number, alpha: number, A: Float32Array, LDA: number, x: Float32Array, strideX: number, beta: number, y: Float32Array, strideY: number ): Float32Array;

	/**
	* Performs one of the matrix-vector operations `y = alpha*A*x + beta*y` where alpha and beta are scalars, x and y are n element vectors and A is an n by n symmetric matrix using alternative indexing semantics.
	*
	* @param order - storage layout
	* @param uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is being supplied
	* @param N - number of columns in the matrix `A`
	* @param alpha - scalar constant
	* @param A - matrix of coefficients
	* @param LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
	* @param x - an `M` element vector
	* @param strideX - `x` stride length
	* @param offsetX - `x` index offset
	* @param beta - scalar constant
	* @param y - an `N` element vector
	* @param strideY - `y` stride length
	* @param offsetY - `y` index offset
	* @returns output array `y`
	*
	* @example
	* var Float32Array = require( '@stdlib/array/float32' );
	*
	* var uplo = 'L';
	* var order = 'row-major';
	*
	* var N = 3;
	* var lda = 3;
	*
	* var alpha = 1.0;
	* var beta = 0.0;
	*
	* var A = new Float32Array( [ 1.0, 0.0, 0.0, 2.0, 4.0, 0.0, 3.0, 5.0, 6.0 ] );
	* var x = new Float32Array( [ 1.0, 1.0, 1.0 ] );
	* var y = new Float32Array( [ 0.0, 0.0, 0.0 ] );
	*
	* ssymv.ndarray( order, uplo, N, alpha, A, lda, x, 1, 0, beta, y, 1, 0 );
	* // y => <Float32Array>[ 6.0, 11.0, 14.0 ]
	*/
	ndarray( order: string, uplo: string, N: number, alpha: number, A: Float32Array, LDA: number, x: Float32Array, strideX: number, offsetX: number, beta: number, y: Float32Array, strideY: number, offsetY: number ): Float32Array;
}

/**
* Performs one of the matrix-vector operations `y = alpha*A*x + beta*y` where alpha and beta are scalars, x and y are n element vectors and A is an n by n symmetric matrix.
*
* @param order - storage layout
* @param uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is being supplied
* @param N - number of columns in the matrix `A`
* @param alpha - scalar constant
* @param A - matrix of coefficients
* @param LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @param x - an `M` element vector
* @param strideX - `x` stride length
* @param beta - scalar constant
* @param y - an `N` element vector
* @param strideY - `y` stride length
* @returns output array `y`
*
* @example
* var Float32Array = require( '@stdlib/array/float32' );
*
* var uplo = 'U';
* var order = 'row-major';
*
* var N = 3;
* var lda = 3;
*
* var alpha = 2.0;
* var beta = 1.0;
*
* var A = new Float32Array( [ 1.0, 2.0, 3.0, 0.0, 4.0, 5.0, 0.0, 0.0, 6.0 ] );
* var x = new Float32Array( [ 1.0, 2.0, 3.0 ] );
* var y = new Float32Array( [ 1.0, 2.0, 3.0 ] );
*
* ssymv( order, uplo, N, alpha, A, lda, x, -1, beta, y, 1 );
* // y => <Float32Array>[ 21.0, 40.0, 53.0 ]
*
* @example
* var Float32Array = require( '@stdlib/array/float32' );
*
* var uplo = 'U';
* var order = 'row-major';
*
* var N = 3;
* var lda = 3;
*
* var alpha = 2.0;
* var beta = 1.0;
*
* var A = new Float32Array( [ 1.0, 2.0, 3.0, 0.0, 4.0, 5.0, 0.0, 0.0, 6.0 ] );
* var x = new Float32Array( [ 1.0, 2.0, 3.0 ] );
* var y = new Float32Array( [ 1.0, 2.0, 3.0 ] );
*
* ssymv.ndarray( order, uplo, N, alpha, A, lda, x, -1, 2, beta, y, 1, 0 );
* // y => <Float32Array>[ 21.0, 40.0, 53.0 ]
*/
declare var ssymv: Routine;


// EXPORTS //

export = ssymv;
