'use strict';

// TODO: move this to a new `@stdlib/blas/base/assert/is-uplo` package


// MAIN //

/**
* Tests whether a provided value is a valid "uplo" parameter.
*
* @private
* @param {string} value - input value
* @returns {boolean} boolean indicating whether an input value is valid
*
* @example
* var bool = isUPLO( 'upper' );
* // returns true
*
* @example
* var bool = isUPLO( 'foo' );
* // returns false
*/
function isUPLO( value ) {
	return ( value === 'upper' || value === 'lower' );
}


// EXPORTS //

module.exports = isUPLO;
