#include "stdlib/blas/base/ssyr.h"
#include "stdlib/blas/base/shared.h"

/**
* Performs the symmetric rank 1 operation `A = Î±*x*x^T + A`.
*
* @param order    storage layout
* @param uplo     specifies whether the upper or lower triangular part of the symmetric matrix `A` should be referenced
* @param N        number of elements along each dimension of `A`
* @param alpha    scalar
* @param x        input vector
* @param strideX  `x` stride length
* @param A        input matrix
* @param LDA      stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
*/
void API_SUFFIX(c_ssyr)( const CBLAS_LAYOUT order, const CBLAS_UPLO uplo, const CBLAS_INT N, const float alpha, const float *X, const CBLAS_INT strideX, float *A, const CBLAS_INT LDA ) {
	CBLAS_INT ix0;
	CBLAS_INT ix1;
	CBLAS_INT i0;
	CBLAS_INT i1;
	CBLAS_INT ox;
	float tmp;

	if ( N == 0 || alpha == 0.0f ) {
		return;
	}
	ox = STDLIB_BLAS_BASE_STRIDE2OFFSET( N, strideX );
	if (
		( order == CblasRowMajor && uplo == CblasLower ) ||
		( order == CblasColMajor && uplo == CblasUpper )
	) {
		ix1 = ox;
		for ( i1 = 0; i1 < N; i1++ ) {
			if ( X[ ix1 ] != 0.0f ) {
				tmp = alpha * X[ ix1 ];
				// oa = (sa1*i1);
				ix0 = ox;
				for ( i0 = 0; i0 <= i1; i0++ ) {
					A[ ( LDA*i1 )+i0 ] += ( X[ ix0 ] * tmp );
					ix0 += strideX;
				}
			}
			ix1 += strideX;
		}
		return;
	}
	// ( order == CblasRowMajor && uplo == CblasUpper ) || ( order == CblasColMajor && uplo == CblasLower )
	ix1 = ox;
	for ( i1 = 0; i1 < N; i1++ ) {
		if ( X[ ix1 ] != 0.0f ) {
			tmp = alpha * X[ ix1 ];
			ix0 = ix1;
			for ( i0 = i1; i0 < N; i0++ ) {
				A[ ( LDA*i1 )+i0 ] += ( X[ ix0 ] * tmp );
				ix0 += strideX;
			}
		}
		ix1 += strideX;
	}
	return;

}
