'use strict';

// MODULES //

var f32 = require( '@stdlib/number/float64/base/to-float32' );
var max = require( '@stdlib/math/base/special/max' );
var isLayout = require( '@stdlib/blas/base/assert/is-layout' );
var isMatrixTriangle = require( '@stdlib/blas/base/assert/is-matrix-triangle' );
var isTransposeOperation = require( '@stdlib/blas/base/assert/is-transpose-operation');
var isDiagonal = require( '@stdlib/blas/base/assert/is-diagonal-type' );


// MAIN //

/**
* Performs one of the matrix-vector operations `x = A*x`, or `x = A**T*x`, where `x` is an `N` element vector, and `A` is an `N` by `N` unit, or non-unit, upper or lower triangular matrix.
*
* @param {string} order - storage layout
* @param {string} uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is supplied
* @param {string} trans - specifies whether the matrix `A` is a none-transpose, transpose, or conjugate-transpose
* @param {string} diag - specifies whether or not the matrix `A` is a unit triangular
* @param {NonNegativeInteger} N - number of elements along each dimension of `A`
* @param {Float32Array} A - matrix
* @param {integer} LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @param {NonNegativeInteger} offsetA - index offset for `A`
* @param {Float32Array} x - first input array
* @param {integer} strideX - `x` stride length
* @param {NonNegativeInteger} offsetX - index offset for `x`
* @throws {TypeError} first argument must be a valid order
* @throws {TypeError} second argument must specify whether the lower or upper triangular matrix is supplied
* @throws {RangeError} third argument must be a nonnegative integer
* @throws {RangeError} seventh argument must be non-zero
* @throws {RangeError} tenth argument must be non-zero
* @returns {Float32Array} `x`
*
* @example
* var Float32Array = require( '@stdlib/array/float32' );
*
* var A = new Float32Array( [ 1.0, 2.0, 3.0, 0.0, 1.0, 2.0, 0.0, 0.0, 1.0 ] );
* var x = new Float32Array( [ 1.0, 2.0, 3.0 ] );
*
* strmv( 'row-major', 'upper', 'none', 'unit', 3, A, 3, 0, x, 1, 0 );
* // x => <Float32Array>[ 14.0, 8.0, 3.0 ]
*/
function strmv( order, uplo, trans, diag, N, A, LDA, offsetA, x, strideX, offsetX ) { // eslint-disable-line max-params, max-len
	var temp;
	var ix;
	var jx;
	var kx;
	var i;
	var j;

	if ( !isLayout( order ) ) {
		throw new TypeError( 'invalid argument. First argument must be a valid order. Value: `%s`.', order );
	}
	if ( !isMatrixTriangle( uplo ) ) {
		throw new TypeError( 'invalid argument. Second argument must specify whether the lower or upper triangular matrix is supplied. Value: `%s`.', uplo );
	}
	if ( !isTransposeOperation( trans ) ) {
		throw new TypeError( 'invalid argument. Third argument must specify whether the matrix is none-transpose, transpose, or conjugate-transpose. Value: `%s`.', trans );
	}
	if ( !isDiagonal( diag ) ) {
		throw new TypeError( 'invalid argument. Fourth argument must be a valid diagonal type. Value: `%s`.', diag );
	}
	if ( N < 0 ) {
		throw new RangeError( 'invalid argument. Fifth argument must be a nonnegative integer. Value: `%d`.', N );
	}
	if ( LDA < max( 1, N ) ) {
		throw new RangeError( 'invalid argument. Sixth argument must be greater than or equal to the maximum of 1 and the number of elements in the first dimension of `A`. Value: `%d`.', LDA );
	}
	if ( strideX === 0 ) {
		throw new RangeError( 'invalid argument. Seventh argument must be non-zero. Value: `%d`.', strideX );
	}
	if ( N === 0 ) {
		return x;
	}
	kx = offsetX;
	if (
		( order === 'column-major' && trans === 'none' && uplo === 'upper' ) ||
		( order === 'row-major' && trans !== 'none' && uplo === 'lower' )
	) {
		jx = kx;
		for ( j = 0; j < N; j++ ) {
			if ( x[ jx ] !== 0.0 ) {
				temp = x[ jx ];
				ix = kx;
				for ( i = 0; i < j; i++ ) {
					x[ ix ] += f32( temp * A[ offsetA + i + ( LDA * j ) ] );
					ix += strideX;
				}
				if ( diag === 'non-unit' ) {
					x[ jx ] = f32( x[ jx ] * A[ offsetA + j + ( j * LDA ) ] );
				}
			}
			jx += strideX;
		}
		return x;
	}
	if (
		( order === 'column-major' && trans === 'none' && uplo === 'lower' ) ||
		( order === 'row-major' && trans !== 'none' && uplo === 'upper' )
	) {
		kx += ( N - 1 ) * strideX;
		jx = kx;
		for ( j = N - 1; j >= 0; j-- ) {
			if ( x[ jx ] !== 0.0 ) {
				temp = x[ jx ];
				ix = kx;
				for ( i = N - 1; i > j; i-- ) {
					x[ ix ] += f32( temp * A[ offsetA + i + ( LDA * j ) ] );
					ix -= strideX;
				}
				if ( diag === 'non-unit' ) {
					x[ jx ] = f32( x[ jx ] * A[ offsetA + j + ( j * LDA ) ] );
				}
			}
			jx -= strideX;
		}
		return x;
	}
	if (
		( order === 'column-major' && trans !== 'none' && uplo === 'upper' ) ||
		( order === 'row-major' && trans === 'none' && uplo === 'lower' )
	) {
		jx = kx + ( ( N - 1 ) * strideX );
		for ( j = N - 1; j >= 0; j-- ) {
			temp = x[ jx ];
			ix = jx;
			if ( diag === 'non-unit' ) {
				temp = f32( temp * A[ offsetA + j + ( j * LDA ) ] );
			}
			for ( i = j - 1; i >= 0; i-- ) {
				ix -= strideX;
				temp = f32( temp + ( x[ ix ] * A[ offsetA + i + ( LDA * j ) ] ) ); // eslint-disable-line max-len
			}
			x[ jx ] = temp;
			jx -= strideX;
		}
		return x;
	}
	if (
		( order === 'column-major' && trans !== 'none' && uplo === 'lower' ) ||
		( order === 'row-major' && trans === 'none' && uplo === 'upper' )
	) {
		jx = kx;
		for ( j = 0; j < N; j++ ) {
			temp = x[ jx ];
			ix = jx;
			if ( diag === 'non-unit' ) {
				temp = f32( temp * A[ offsetA + j + ( j * LDA ) ] );
			}
			for ( i = j + 1; i < N; i++ ) {
				ix += strideX;
				temp = f32( temp + ( x[ ix ] * A[ offsetA + i + ( LDA * j ) ] ) ); // eslint-disable-line max-len
			}
			x[ jx ] = temp;
			jx += strideX;
		}
		return x;
	}
}


// EXPORTS //

module.exports = strmv;
