'use strict';

// MODULES //

var f32 = require( '@stdlib/number/float64/base/to-float32' );
var max = require( '@stdlib/math/base/special/max' );
var isLayout = require( '@stdlib/blas/base/assert/is-layout' );
var isMatrixTriangle = require( '@stdlib/blas/base/assert/is-matrix-triangle' );
var isTransposeOperation = require( '@stdlib/blas/base/assert/is-transpose-operation');
var isDiagonal = require( '@stdlib/blas/base/assert/is-diagonal-type' );
var stride2offset = require( '@stdlib/strided/base/stride2offset');
var format = require( '@stdlib/string/format' );


// MAIN //

/**
* Performs one of the matrix-vector operations `x = A*x` or `x = A**T*x`, where `x` is an `N` element vector and `A` is an `N` by `N` unit, or non-unit, upper or lower triangular matrix.
*
* @param {string} order - storage layout
* @param {string} uplo - specifies whether the upper or lower triangular part of the symmetric matrix `A` is supplied
* @param {string} trans - specifies whether the matrix `A` is transposed, conjugate-transposed, or not transposed
* @param {string} diag - specifies whether the matrix `A` has a unit diagonal
* @param {NonNegativeInteger} N - number of elements along each dimension of `A`
* @param {Float32Array} A - input matrix
* @param {integer} LDA - stride of the first dimension of `A` (a.k.a., leading dimension of the matrix `A`)
* @param {Float32Array} x - input vector
* @param {integer} strideX - `x` stride length
* @throws {TypeError} first argument must be a valid order
* @throws {TypeError} second argument must specify whether the lower or upper triangular matrix is supplied
* @throws {TypeError} third argument must be a valid transpose operation
* @throws {TypeError} fourth argument must be a valid diagonal type
* @throws {RangeError} fifth argument must be a nonnegative integer
* @throws {RangeError} seventh argument must be greater than or equal to max(1,N)
* @throws {RangeError} ninth argument must be non-zero
* @returns {Float32Array} `x`
*
* @example
* var Float32Array = require( '@stdlib/array/float32' );
*
* var A = new Float32Array( [ 1.0, 2.0, 3.0, 0.0, 1.0, 2.0, 0.0, 0.0, 1.0 ] );
* var x = new Float32Array( [ 1.0, 2.0, 3.0 ] );
*
* strmv( 'row-major', 'upper', 'none', 'unit', 3, A, 3, x, 1 );
* // x => <Float32Array>[ 14.0, 8.0, 3.0 ]
*/
function strmv( order, uplo, trans, diag, N, A, LDA, x, strideX ) {
	var nonunit;
	var temp;
	var ix;
	var jx;
	var kx;
	var i;
	var j;

	if ( !isLayout( order ) ) {
		throw new TypeError( format( 'invalid argument. First argument must be a valid order. Value: `%s`.', order ) );
	}
	if ( !isMatrixTriangle( uplo ) ) {
		throw new TypeError( format( 'invalid argument. Second argument must specify whether the lower or upper triangular matrix is supplied. Value: `%s`.', uplo ) );
	}
	if ( !isTransposeOperation( trans ) ) {
		throw new TypeError( format( 'invalid argument. Third argument must be a valid transpose operation. Value: `%s`.', trans ) );
	}
	if ( !isDiagonal( diag ) ) {
		throw new TypeError( format( 'invalid argument. Fourth argument must be a valid diagonal type. Value: `%s`.', diag ) );
	}
	if ( N < 0 ) {
		throw new RangeError( format( 'invalid argument. Fifth argument must be a nonnegative integer. Value: `%d`.', N ) );
	}
	if ( LDA < max( 1, N ) ) {
		throw new RangeError( format( 'invalid argument. Seventh argument must be greater than or equal to max(1,%d). Value: `%d`.', N, LDA ) );
	}
	if ( strideX === 0 ) {
		throw new RangeError( format( 'invalid argument. Ninth argument must be non-zero. Value: `%d`.', strideX ) );
	}
	if ( N === 0 ) {
		return x;
	}
	nonunit = ( diag === 'non-unit' );
	kx = stride2offset( N, strideX );
	if (
		( order === 'column-major' && trans === 'none' && uplo === 'upper' ) ||
		( order === 'row-major' && trans !== 'none' && uplo === 'lower' )
	) {
		jx = kx;
		for ( j = 0; j < N; j++ ) {
			if ( x[ jx ] !== 0.0 ) {
				temp = x[ jx ];
				ix = kx;
				for ( i = 0; i < j; i++ ) {
					x[ ix ] += f32( temp * A[ i+(LDA*j) ] );
					ix += strideX;
				}
				if ( nonunit ) {
					x[ jx ] = f32( x[ jx ] * A[ (j*LDA)+j ] );
				}
			}
			jx += strideX;
		}
		return x;
	}
	if (
		( order === 'column-major' && trans === 'none' && uplo === 'lower' ) ||
		( order === 'row-major' && trans !== 'none' && uplo === 'upper' )
	) {
		kx += ( N - 1 ) * strideX;
		jx = kx;
		for ( j = N - 1; j >= 0; j-- ) {
			if ( x[ jx ] !== 0.0 ) {
				temp = x[ jx ];
				ix = kx;
				for ( i = N - 1; i > j; i-- ) {
					x[ ix ] += f32( temp * A[ i+(LDA*j) ] );
					ix -= strideX;
				}
				if ( nonunit ) {
					x[ jx ] = f32( x[ jx ] * A[ (j*LDA)+j ] );
				}
			}
			jx -= strideX;
		}
		return x;
	}
	if (
		( order === 'column-major' && trans !== 'none' && uplo === 'upper' ) ||
		( order === 'row-major' && trans === 'none' && uplo === 'lower' )
	) {
		jx = kx + ( ( N - 1 ) * strideX );
		for ( j = N - 1; j >= 0; j-- ) {
			temp = x[ jx ];
			ix = jx;
			if ( nonunit ) {
				temp = f32( temp * A[ (j*LDA)+j ] );
			}
			for ( i = j - 1; i >= 0; i-- ) {
				ix -= strideX;
				temp = f32( temp + f32( x[ ix ] * A[ i+(LDA*j) ] ) );
			}
			x[ jx ] = temp;
			jx -= strideX;
		}
		return x;
	}
	if (
		( order === 'column-major' && trans !== 'none' && uplo === 'lower' ) ||
		( order === 'row-major' && trans === 'none' && uplo === 'upper' )
	) {
		jx = kx;
		for ( j = 0; j < N; j++ ) {
			temp = x[ jx ];
			ix = jx;
			if ( nonunit ) {
				temp = f32( temp * A[ (j*LDA)+j ] );
			}
			for ( i = j + 1; i < N; i++ ) {
				ix += strideX;
				temp = f32( temp + f32( x[ ix ] * A[ i+(LDA*j) ] ) );
			}
			x[ jx ] = temp;
			jx += strideX;
		}
		return x;
	}
}


// EXPORTS //

module.exports = strmv;
