#include "stdlib/blas/base/zdrot.h"
#include "stdlib/blas/base/shared.h"

/**
* Applies a plane rotation on complex double-precision floating-point vectors.
*
* @param N        number of indexed elements
* @param ZX        first input array
* @param strideX  ZX stride length
* @param ZY        second input array
* @param strideY  ZY stride length
* @param c        cosine of the angle of rotation
* @param s        sine of the angle of rotation
*/
void API_SUFFIX(c_zdrot)( const CBLAS_INT N, void *ZX, const CBLAS_INT strideX, void *ZY, const CBLAS_INT strideY, const double c, const double s ) {
	double *zx = (double *)ZX;
	double *zy = (double *)ZY;
	double tmp1;
	double tmp2;
	CBLAS_INT ix;
	CBLAS_INT iy;
	CBLAS_INT sx;
	CBLAS_INT sy;
	CBLAS_INT i;
	CBLAS_INT j;

	if ( N <= 0 ) {
		return;
	}
	if ( strideX == 1 && strideY == 1 ) {
		for ( i = 0; i < N*2; i += 2 ) {
			tmp1 = c*zx[ i ] + s*zy[ i ];
			zy[ i ] = c*zy[ i ] - s*zx[ i ];
			zx[ i ] = tmp1;

			j = i + 1;
			tmp2 = c*zx[ j ] + s*zy[ j ];
			zy[ j ] = c*zy[ j ] - s*zx[ j ];
			zx[ j ] = tmp2;
		}
		return;
	}
	if ( strideX < 0 ) {
		ix = 2 * (1-N) * strideX;
	} else {
		ix = 0;
	}
	if ( strideY < 0 ) {
		iy = 2 * (1-N) * strideY;
	} else {
		iy = 0;
	}
	sx = strideX * 2;
	sy = strideY * 2;
	for ( i = 0; i < N; i++ ) {
		tmp1 = c*zx[ ix ] + s*zy[ iy ];
		zy[ iy ] = c*zy[ iy ] - s*zx[ ix ];
		zx[ ix ] = tmp1;

		tmp2 = c*zx[ ix+1 ] + s*zy[ iy+1 ];
		zy[ iy+1 ] = c*zy[ iy+1 ] - s*zx[ ix+1 ];
		zx[ ix+1 ] = tmp2;

		ix += sx;
		iy += sy;
	}
	return;
}
