#include "stdlib/math/base/special/gcd.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/math/base/assert/is_integer.h"
#include "stdlib/constants/float64/pinf.h"
#include "stdlib//constants/float64/ninf.h"
#include "stdlib/constants/int32/max.h"

/**
* Computes the greatest common divisor (gcd) using the binary GCD algorithm.
*
* @param a    input value
* @param b    input value
* @return	  output value
*
* @example
* double out = largeIntegers( 1.2676506002282294e+30, 9007199254740992 );
* // returns 9007199254740992.0
*/

double largeIntegers( const int32_t a, const int32_t b ) {
	int32_t k = 1;
	int32_t t;
	int32_t ac;
	int32_t bc;

	ac = a;
	bc = b;

	// Simple cases:
	if ( ac == 0 ) {
		return bc;
	}
	if ( bc == 0 ) {
		return ac;
	}
	// Reduce `a` and/or `b` to odd numbers and keep track of the greatest power of 2 dividing both `a` and `b`...
	while ( ac%2 == 0 && bc%2 == 0 ) {
		ac /= 2; // right shift
		bc /= 2; // right shift
		k *= 2; // left shift
	}
	// Reduce `a` to an odd number...
	while ( ac%2 == 0 ) {
		ac /= 2; // right shift
	}
	// Henceforth, `a` is always odd...
	while ( bc ) {
		// Remove all factors of 2 in `b`, as they are not common...
		while ( bc%2 == 0 ) {
			bc /= 2; // right shift
		}
		// `a` and `b` are both odd. Swap values such that `b` is the larger of the two values, and then set `b` to the difference (which is even)...
		if ( ac > bc ) {
			t = bc;
			bc = ac;
			ac = t;
		}
		bc -= ac; // b=0 iff b=a
	}
	// Restore common factors of 2...
	return k * ac;
}

/**
* Computes the greatest common divisor (gcd) using the binary GCD algorithm and bitwise operations.
*
* @param a    input value
* @param b    input value
* @return	  output value
*
* @example
* double out = bitwise( 48, 18 );
* // returns 6.0
*/

double bitwise( const int32_t a, const int32_t b ) {
	int32_t k = 0;
	int32_t t;
	int32_t ac;
	int32_t bc;

	ac = a;
	bc = b;

	// Simple cases:
	if ( ac == 0 ) {
		return b;
	}
	if ( bc == 0 ) {
		return a;
	}
	// Reduce `a` and/or `b` to odd numbers and keep track of the greatest power of 2 dividing both `a` and `b`...
	while ( (ac & 1) == 0 && (bc & 1) == 0 ) {
		ac >>= 1; // right shift
		bc >>= 1; // right shift
		k += 1;
	}
	// Reduce `a` to an odd number...
	while ( (ac & 1) == 0 ) {
		ac >>= 1; // right shift
	}
	// Henceforth, `a` is always odd...
	while ( bc ) {
		// Remove all factors of 2 in `b`, as they are not common...
		while ( (bc & 1) == 0 ) {
			bc >>= 1; // right shift
		}
		// `a` and `b` are both odd. Swap values such that `b` is the larger of the two values, and then set `b` to the difference (which is even)...
		if ( ac > bc ) {
			t = bc;
			bc = ac;
			ac = t;
		}
		bc -= ac; // b=0 iff b=a
	}
	// Restore common factors of 2...
	return ac << k;
}

/**
* Computes the greatest common divisor (gcd).
*
* @param a    input value
* @param b    input value
* @return	  output value
*
* @example
* double out = stdlib_base_gcd( 48, 18 );
* // returns 6.0
*/

double stdlib_base_gcd( const int32_t a, const int32_t b ) {
	int32_t ac;
	int32_t bc;

	ac = a;
	bc = b;

	if ( stdlib_base_is_nan( ac ) || stdlib_base_is_nan( bc ) ) {
		return 0.0/0.0;
	}
	if (
		ac == STDLIB_CONSTANT_FLOAT64_PINF ||
		bc == STDLIB_CONSTANT_FLOAT64_PINF ||
		ac == STDLIB_CONSTANT_FLOAT64_NINF ||
		bc == STDLIB_CONSTANT_FLOAT64_PINF
	) {
		return 0.0/0.0;
	}
	if ( !( stdlib_base_is_integer( ac ) && stdlib_base_is_integer( bc ) ) ) {
		return 0.0/0.0;
	}
	if ( ac < 0 ) {
		ac = -ac;
	}
	if ( bc < 0 ) {
		bc = -bc;
	}
	if ( ac <= STDLIB_CONSTANT_INT32_MAX && bc <= STDLIB_CONSTANT_INT32_MAX ) {
		return bitwise( ac, bc );
	}
	return largeIntegers( ac, bc );
}
